$pjs.views.translateEffects = function(efectos) {
    if (efectos) {
        $.each($pjs.views.translateDict, function(k, v) {
            efectos = efectos.replace(k, '<i title="' + v.name + '" class="ra ' + v.icon + '"></i>');
        });

        return efectos;

    } else {

        return "";
    }

};
